mapper.update:
/* QUERY ID : updateSendEmailResult */    
   UPDATE rpc_cmm_email SET
               result_code = #{resultCode},
               result_message = #{resultMessage},
               belonging_email_cnt = #{belongingEmailCnt}
   WHERE email_id =  #{emailId} AND seq =  #{seq}

mapper.delete:
/* QUERY ID : deleteEmail */
   DELETE FROM rpc_cmm_email
    WHERE email_id = #{emailId}  AND seq = #{seq}

mapper.select:
/* QUERY ID : getEmailAttachedFileListByFileIDs */ SELECT a.file_id 'fileId', a.saved_filename 'savedFilename', a.original_filename 'originalFilename', a.file_type 'fileType', a.file_size 'fileSize', a.file_description 'fileDescription', a.create_datetime 'createDatetime', a.creator 'creator' FROM rpc_cmm_email_attached_file a WHERE 1 = 1
AND a.file_id IN
#{item}
ORDER BY a.file_id ASC

mapper.delete:
 /* QUERY ID : deleteEmailAttachedFile */
 DELETE FROM rpc_cmm_email_attached_file
WHERE file_id IN


              #{item}
