mapper.insert:
     /* QUERY ID : registNoticeDiscuss */
		INSERT INTO cmm_notice_discuss(id
							,category
							,subject
							,contents
							,writer
							,write_from
							,write_to
							,is_for_all_seller
							,is_for_all_customer
							,is_for_notice
							,creator)     
		VALUES (#{id}
		    ,#{category}
		    ,#{subject}
		    ,#{contents}
		    ,#{writer}
		    ,#{writeFrom}
		    ,#{writeTo}
		    ,#{isForAllSeller}
		    ,#{isForAllCustomer}
		    ,#{isForNotice}
		    ,#{creator} )

mapper.update:
     /* QUERY ID : modifyNoticeDiscuss */    
		UPDATE cmm_notice_discuss
		   SET create_datetime = CURRENT_TIMESTAMP
		   	    , category = #{category}
		   	    , subject = TRIM(#{subject})
		   	    , contents = TRIM(#{contents})
		   	    , writer = #{writer}
		   	    , write_from = #{writeFrom}
		   	    , write_to = #{writeTo}
		   	    , is_for_all_seller = UPPER(#{isForAllSeller})
		   	    , is_for_all_customer = UPPER(#{isForAllCustomer})
		   	    , is_for_notice = UPPER(#{isForNotice})
		   	    , creator = #{creator}
        WHERE id =  #{id}

     /* QUERY ID : modifyNoticeDiscussForNotNull */    
		UPDATE cmm_notice_discuss
		   SET create_datetime = CURRENT_TIMESTAMP
		   	   , category = #{category}
		   	   , subject = TRIM(#{subject})
		   	   , contents = TRIM(#{contents})
		   	   , writer = #{writer}
		   	   , write_from = #{writeFrom}
		   	   , write_to = #{writeTo}
		   	   , is_for_all_seller = UPPER(#{isForAllSeller})
		   	   , is_for_all_customer = UPPER(#{isForAllCustomer})
		   	   , is_for_notice = UPPER(#{isForNotice})
		   	   , creator = #{creator}
        WHERE id =  #{id}
