mapper.insert:
     /* QUERY ID : registNoticeDiscuss */
		INSERT INTO cmm_qna(
		  					 customer_mobile
							,customer_email
							,customer_question
							,qna_status
							,creator)
		VALUES (
		     #{customerMobile}
		    ,#{customerEmail}
		    ,#{customerQuestion}
		    ,#{qnaStatus}
		    ,#{creator} )

mapper.update:
     /* QUERY ID : modifyQnA */
		UPDATE cmm_qna
		   SET create_datetime = CURRENT_TIMESTAMP
		   	   , customer_mobile = #{customerMobile}
		   	   , customer_email = TRIM(#{customerEmail})
		   	   , customer_question = TRIM(#{customerQuestion})
		   	   , qna_status = TRIM(#{qnaStatus})
		   	   , creator = #{creator}
        WHERE id =  #{id}

     /* QUERY ID : modifyQnAForNotNull */
		UPDATE cmm_qna
		   SET create_datetime = CURRENT_TIMESTAMP
		   	   , customer_mobile = #{customerMobile}
		   	   , customer_email = TRIM(#{customerEmail})
		   	   , customer_question = TRIM(#{customerQuestion})
		   	   , qna_status = TRIM(#{qnaStatus})
		   	   , creator = #{creator}
        WHERE id =  #{id}
