mapper.select:
/* QUERY ID : getSystemUseProfileList */ SELECT a.seq 'seq', a.user_id 'userId', a.login_ip_address 'loginIPAddress', a.login_datetime 'loginDatetime', a.logout_datetime 'logoutDatetime', a.minutes_of_use 'minutesOfUse', DATE_FORMAT(a.create_dt, '%d/%m/%Y %H:%i') 'createDt' FROM rpc_cmm_system_useprofile a WHERE 1 = 1 AND a.userId like #{userId}
AND (a.login_datetime >= str_to_date(#{periodFrom}, '%d/%m/%Y') AND a.login_datetime <= str_to_date(#{periodTo}, '%d/%m/%Y') )
AND (a.logout_datetime >= str_to_date(#{periodFrom}, '%d/%m/%Y') AND a.logout_datetime <= str_to_date(#{periodTo}, '%d/%m/%Y') )
