mapper.select:
/* QUERY ID : getDeliveryCalendarsForGuestOrder */ SELECT a.delivery_seq 'deliverySeq', a.seller_id 'sellerId', a.yyyy_mm_dd 'yyyyMmDd', a.delivery_seq 'deliverySeq', a.address_postcode 'addressPostcode', a.address_state 'addressState', a.address_suburb 'addressSuburb', a.address_street 'addressStreet', a.address_note 'addressNote', a.is_pickup 'isPickup', b.user_name 'seller.userName', a.btwn_from_hhmm 'btwnFromHhmm', a.btwn_to_hhmm 'btwnToHhmm', a.use_yn 'useYn', a.create_datetime 'createDatetime', a.modify_datetime 'modifyDatetime', a.creator 'creator' FROM shop_delivery_calendar a LEFT OUTER JOIN cmm_user b ON b.user_id = a.seller_id WHERE UPPER(a.use_yn) = 'Y' AND a.seller_id = #{sellerId} AND str_to_date(#{searchDateFrom}, '%Y-%m-%d') <= str_to_date(a.yyyy_mm_dd, '%Y-%m-%d')
AND ( a.is_pickup = 'Y' OR ( a.address_postcode = #{addressPostcode} AND UPPER(a.address_suburb) LIKE CONCAT('%', UPPER(#{addressSuburb}), '%') UPPER(a.address_suburb) LIKE CONCAT('%', UPPER(#{addressSuburb}), '%') ) )
ORDER BY a.is_pickup DESC, a.yyyy_mm_dd ASC, a.btwn_from_hhmm ASC, a.address_suburb ASC LIMIT #{pagenationPage}, #{pagenationPageSize}

	 /* QUERY ID : getTotalCntForDeliveryCalendarsForGuestOrder */
		SELECT COUNT(*)
	  FROM shop_delivery_calendar a
		  		LEFT OUTER JOIN cmm_user b ON b.user_id = a.seller_id
  	     WHERE UPPER(a.use_yn) = 'Y'
	  	      AND a.seller_id = #{sellerId} 	 
			  AND str_to_date(#{searchDateFrom}, '%Y-%m-%d')  <= str_to_date(a.yyyy_mm_dd, '%Y-%m-%d') 


 	     	 			AND (
 	     	 			a.is_pickup = 'Y' OR (
		  	     		 a.address_postcode = #{addressPostcode} 	 
		  	     		 AND UPPER(a.address_suburb) LIKE CONCAT('%', UPPER(#{addressSuburb}), '%')  	 
		  	     		 UPPER(a.address_suburb) LIKE CONCAT('%', UPPER(#{addressSuburb}), '%')  
		  	     		)	 
	  	     		)

mapper.insert:
 /* QUERY ID : insertDeliveryCalendar */
	INSERT INTO shop_delivery_calendar (seller_id,
	                    yyyy_mm_dd,
	                    delivery_seq,
	                    btwn_from_hhmm,
	                    btwn_to_hhmm,
	                    is_pickup,
	                    address_postcode,
	                    address_state,
	                    address_suburb,
	                    address_street,
	                    address_note,
	                    use_yn,
	                    creator)
	VALUES (#{sellerId}
        ,#{yyyyMmDd}
        ,#{deliverySeq}
        ,TRIM(#{btwnFromHhmm})
        ,TRIM(#{btwnToHhmm})
        ,UPPER(#{isPickup})
        ,#{addressPostcode}
        ,#{addressState}
        ,#{addressSuburb}
        ,#{addressStreet}
        ,#{addressNote}
        ,UPPER(#{useYn})
        ,#{creator})
