mapper.update:
/* QUERY ID : modifyOrderProductOption */
UPDATE order_product_option
   SET modify_datetime = CURRENT_TIMESTAMP
       ,customer_id = #{customerId}
       ,seller_id = #{sellerId}
       ,prod_id = #{prodId}
       ,amount = #{amount}
 WHERE order_item_id = #{orderItemId}  AND seq = #{seq}
