mapper.select:
		/* QUERY ID : getTotalCntForShoppingCartProducts */
			SELECT COUNT(*)
             FROM shopping_cart a
	         WHERE 1 = 1
				 AND a.id = #{id} 
				 AND UPPER(a.customer_id) = UPPER(#{customerId}) 
				 AND UPPER(a.seller_id) = UPPER(#{sellerId}) 
				 AND a.prod_id = #{prodId} 
				 AND UPPER(a.creator) = UPPER(#{creator})

mapper.insert:
	/* QUERY ID : insertShoppingCart */
	INSERT INTO shopping_cart(customer_id
                     ,seller_id
                     ,prod_id
                     ,amount
                     ,creator)
	   VALUES ( #{customerId}
	             ,#{sellerId}
	             ,#{prodId}
	             ,#{amount}
	             ,#{creator})


	    SELECT LAST_INSERT_ID() as id

mapper.update:
/* QUERY ID : modifyShoppingCart */
UPDATE shopping_cart
   SET modify_datetime = CURRENT_TIMESTAMP
       ,customer_id = #{customerId}
       ,seller_id = #{sellerId}
       ,prod_id = #{prodId}
       ,amount = #{amount}
 WHERE id = #{id}
