mapper.select:
	 /* QUERY ID : getTotalCntForPaymentMethods */
		SELECT COUNT(*)
		  FROM shopping_payment_method a LEFT OUTER JOIN cmm_user b ON b.user_id = a.user_id
  	         WHERE 1 = 1
				 AND a.user_id = #{userId} 
				 AND a.method_seq = #{methodSeq} 
				 AND a.payment_method = #{paymentMethod} 
				 AND a.bank_name = #{bankName} 
				 AND a.bank_bsb LIKE CONCAT('%', #{bankBsb}, '%') 
				 AND a.bank_account_no LIKE CONCAT('%', #{bankAccountNo}, '%') 
				 AND a.bank_account_owner_name LIKE CONCAT('%', #{bankAccountOwnerName}, '%') 
				 AND UPPER(a.use_yn) = UPPER(#{useYn}) 
				 AND UPPER(a.creator) = UPPER(#{creator})
